package com.rwn.rwnstudy.activities;import android.os.Build;import androidx.annotation.NonNull;import androidx.appcompat.app.AppCompatActivity;import android.os.Bundle;import android.view.View;import android.widget.Button;import android.widget.EditText;import android.widget.Toast;import com.google.android.gms.tasks.OnCompleteListener;import com.google.android.gms.tasks.Task;import com.google.firebase.auth.FirebaseAuth;import com.google.firebase.database.DatabaseReference;import com.google.firebase.database.FirebaseDatabase;import com.rwn.rwnstudy.R;import java.util.Objects;public class FeedBackActivity extends AppCompatActivity {    EditText editText ;    Button button;    DatabaseReference databaseReference;    FirebaseAuth mAuth;    String CURRENT_USER;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_feed_back);        if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.KITKAT) {            Objects.requireNonNull(getSupportActionBar()).setTitle("Feedback");        }        getSupportActionBar().setDisplayHomeAsUpEnabled(true);        button= findViewById(R.id.button_send_feedback);        databaseReference= FirebaseDatabase.getInstance().getReference();        editText= findViewById(R.id.edit_text_feedback);        mAuth= FirebaseAuth.getInstance();        CURRENT_USER=mAuth.getCurrentUser().getUid();        button.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                String feedback=editText.getText().toString();                if(feedback.equals(null)){                    Toast.makeText(FeedBackActivity.this, "Please write us feedback first then send", Toast.LENGTH_SHORT).show();                }else{                    databaseReference.child("Feedback").child(CURRENT_USER).push().child("feedback").setValue(feedback).addOnCompleteListener(new OnCompleteListener <Void>() {                        @Override                        public void onComplete(@NonNull Task<Void> task) {                            if(task.isSuccessful()){                                Toast.makeText(FeedBackActivity.this, "Thank you For you Valuable feedback ", Toast.LENGTH_SHORT).show();                                finish();                            }                        }                    });                }            }        });    }}